pragma solidity ^0.8.28;

import '@openzeppelin/contracts/proxy/Clones.sol';

library Clone {
	function createClone(
		address _contract,
		address _deployer,
		uint256 _nonce
	) internal returns (address) {
		bytes32 salt = keccak256(abi.encodePacked(_deployer, _nonce));

		return Clones.cloneDeterministic(_contract, salt);
	}
}
","keccak256":"0x8b0884713c27666b2be4bea1d4ef96c6ab5ce2734eaf4501fcd0d28b4eb7f798","license":"MIT"},"contracts/core/libraries/Errors.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract Errors {
	/// ======================
	/// ====== Generic =======
	/// ======================

	error INSUFFICIENT_ALLOWANCE();
	error INSUFFICIENT_FUNDS();
	error MISMATCH();
	error UNAUTHORIZED();

	/// ======================
	/// ======= ZK DAO =======
	/// ======================

	error DAO_NOT_FOUND(uint256 daoId);
	error UNEXPECTED_REQUEST_ID(bytes32 requestId);

	/// ======================
	/// ====== Governor ======
	/// ======================

	error INVALID_VOTE_TYPE();
	error INVALID_PROPOSAL_ID();
	error INVALID_NULLIFIER();
